import { Component } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import * as pdfMake from 'pdfmake/build/pdfMake';
import * as pdfFonts from 'pdfmake/build/vfs_fonts';
import { Customer } from 'src/models/customer.class';
import { AngularFireStorage } from '@angular/fire/compat/storage';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-invoice-to-customer',
  templateUrl: './invoice-to-customer.component.html',
  styleUrls: ['./invoice-to-customer.component.scss']
})
export class InvoiceToCustomerComponent {
  allUsers = [];
  userId: any = '';
  user: Customer = new Customer();
  selectedValue!: string;
  description!: string;
  price!: number;
  newInvoiceNumber: number = 1;
  loading: boolean = false;
  date = new Date().toLocaleDateString();
  positions: any = [];
  position_for_pdf: any = [[{
    text: 'DESCRIPTION',
    fillColor: '#eaf2f5',
    border: [false, true, false, true],
    margin: [0, 5, 0, 5],
    textTransform: 'uppercase',
  },
  {
    text: 'TOTAL',
    border: [false, true, false, true],
    alignment: 'right',
    fillColor: '#eaf2f5',
    margin: [0, 5, 0, 5],
    textTransform: 'uppercase',
  }]];

  public myForm = new FormGroup({
    description: new FormControl(''),
    price: new FormControl(''),
    userID: new FormControl('', Validators.required)
  });

  constructor(private firestore: AngularFirestore, private fireStorage: AngularFireStorage) {
    (pdfMake as any).vfs = pdfFonts.pdfMake.vfs;
  }

  ngOnInit() {
    this.firestore.collection('customer')
      .valueChanges({ idField: 'customIdName' })
      .subscribe((changes: any) => this.allUsers = changes);

    let storageRef = this.fireStorage.storage.ref('invoices/all/');
    storageRef.listAll()
      .then((result) => {
        if (result.items.length > 0) this.newInvoiceNumber = result.items.length + 1;
      });

  }

  getInvoiceNumber() {
    return this.newInvoiceNumber.toString();
  }

  addPosition() {
    this.positions.push({
      description: this.myForm.controls.description.value,
      price: this.myForm.controls.price.value,
    });
    this.myForm.controls.description.setValue('');
    this.myForm.controls.price.setValue('');

  }

  getUser() {
    if (this.myForm.valid) {
      this.firestore
        .collection('customer')
        .doc(this.myForm.controls.userID.value!)
        .valueChanges()
        .subscribe((user: any) => {
          this.user = new Customer(user);
          this.userId = this.myForm.controls.userID.value;
        });
    }
  }

  getTotalPrice() {
    let total = 0;
    this.positions.forEach((position: any) => total += +position.price);
    return total;
  }

  setPositionForPdf() {
    this.positions.forEach((position: any) => {
      this.position_for_pdf.push([{
        text: position.description,
        border: [false, false, false, true],
        margin: [0, 5, 0, 5],
        alignment: 'left',
      },
      {
        border: [false, false, false, true],
        text: position.price.toFixed(2) + ' €',
        fillColor: '#f5f5f5',
        alignment: 'right',
        margin: [0, 5, 0, 5],
      }]);
    });
  }

  async generatePDF() {
    if (this.myForm.valid) {
      this.setPositionForPdf();
      let invoiceNumber = this.newInvoiceNumber;
      let docDefinition: any = {
        pageMargins: [50, 250, 50, 75],
        header: function (currentPage: any) {
          if (currentPage == 1) {
            return {
              columns: [
                [
                  {
                    margin: [50, 21, 0, 0],
                    image: `data:image/png;base64,
                    
                    `,
                    width: 150,
                    alignment: 'right',
                  },
                  {
                    text: 'Königsberger Str. 65 \n 21465 Reinbek',
                    bold: true,
                    fontSize: 10,
                    color: '#333322',
                    alignment: 'right',
                    width: 'auto',
                    columnGap: 10,
                    margin: [0, 10, 50, 0],
                    linebreak: true,
                  },
                  '\n',
                  {
                    margin: [0, 0, 50, 0],
                    stack: [
                      {
                        text: 'Contact person \n',
                        color: '#aaaaab',
                        bold: true,
                        width: '*',
                        fontSize: 10,
                        alignment: 'right',
                        linebreak: true,
                      },
                      {
                        text: 'Marijan Dupkovic',
                        bold: true,
                        color: '#333333',
                        fontSize: 10,
                        alignment: 'right',
                        width: 50,
                      }
                    ],
                  },
                  '\n',
                  {
                    margin: [0, 0, 50, 0],
                    stack: [
                      {
                        text: 'Contact mail \n',
                        color: '#aaaaab',
                        bold: true,
                        width: '*',
                        fontSize: 10,
                        alignment: 'right',
                        linebreak: true,
                      },
                      {
                        text: 'info@marijandupkovic.com',
                        bold: true,
                        color: '#333333',
                        fontSize: 10,
                        alignment: 'right',
                        width: 50,
                      },
                    ],
                  },
                  '\n',
                  {
                    margin: [0, 0, 50, 0],

                    stack: [

                      {
                        text: 'Date Issued',
                        color: '#aaaaab',
                        bold: true,
                        width: '*',
                        fontSize: 12,
                        alignment: 'right',
                      },
                      {
                        text: new Date().toLocaleDateString(),
                        bold: true,
                        color: '#333333',
                        fontSize: 10,
                        alignment: 'right',
                        width: 100,
                      },
                      '\n',
                      {
                        text: 'Invoice Number',
                        color: '#aaaaab',
                        bold: true,
                        width: '*',
                        fontSize: 12,
                        alignment: 'right',
                      },
                      {
                        text: invoiceNumber,
                        bold: true,
                        color: '#333333',
                        fontSize: 10,
                        alignment: 'right',
                        width: 100,
                      },

                    ],
                  },
                  '\n',

                ],
              ],
            };
          } else {
            return {
              columns: [
                [
                  [
                    {
                      margin: [50, 21, 0, 0],
                      image: `data:image/png;base64,
                              
                              
                              
                              
                              
                              
                              
                              
                              
                              
                              
                              
                              
                              
                              
                              
                              
                              `,
                      width: 150,
                      alignment: 'right',
                    },],],],
            };
          }
        },
        content: [


          {
            margin: [0, -125, 0, 0],
            columns: [

              {
                text: this.user['company'],
                bold: true,
                color: '#333333',
                alignment: 'left',
              },
            ],
          },
          '\n',
          {
            columns: [

              {
                text: this.user.street + '\n ' + this.user.zipCode + ' ' + this.user.city,
                style: 'invoiceBillingAddress',
              },
            ],
          },
          '\n\n',
          '\n\n',
          '\n\n',
          '\n\n',
          {
            layout: {
              defaultBorder: false,
              hLineWidth: function (i: any, node: any) {
                return 1;
              },
              vLineWidth: function (i: any, node: any) {
                return 1;
              },
              hLineColor: function (i: any, node: any) {
                if (i === 1 || i === 0) {
                  return '#bfdde8';
                }
                return '#eaeaea';
              },
              vLineColor: function (i: any, node: any) {
                return '#eaeaea';
              },
              hLineStyle: function (i: any, node: any) {
                // if (i === 0 || i === node.table.body.length) {
                return null;
                //}
              },
              // vLineStyle: function (i, node) { return {dash: { length: 10, space: 4 }}; },
              paddingLeft: function (i: any, node: any) {
                return 10;
              },
              paddingRight: function (i: any, node: any) {
                return 10;
              },
              paddingTop: function (i: any, node: any) {
                return 2;
              },
              paddingBottom: function (i: any, node: any) {
                return 2;
              },
              fillColor: function (rowIndex: any, node: any, columnIndex: any) {
                return '#fff';
              },
            },
            table: {
              headerRows: 1,
              widths: ['*', 80],
              body: this.position_for_pdf,
            },
          },
          '\n',
          '\n\n',
          {
            layout: {
              defaultBorder: false,
              hLineWidth: function (i: any, node: any) {
                return 1;
              },
              vLineWidth: function (i: any, node: any) {
                return 1;
              },
              hLineColor: function (i: any, node: any) {
                return '#eaeaea';
              },
              vLineColor: function (i: any, node: any) {
                return '#eaeaea';
              },
              hLineStyle: function (i: any, node: any) {
                // if (i === 0 || i === node.table.body.length) {
                return null;
                //}
              },
              // vLineStyle: function (i, node) { return {dash: { length: 10, space: 4 }}; },
              paddingLeft: function (i: any, node: any) {
                return 10;
              },
              paddingRight: function (i: any, node: any) {
                return 10;
              },
              paddingTop: function (i: any, node: any) {
                return 3;
              },
              paddingBottom: function (i: any, node: any) {
                return 3;
              },
              fillColor: function (rowIndex: any, node: any, columnIndex: any) {
                return '#fff';
              },
            },
            table: {
              headerRows: 1,
              widths: ['*', 'auto'],
              body: [
                [
                  {
                    text: 'Total Amount',
                    bold: true,
                    fontSize: 20,
                    alignment: 'right',
                    border: [false, false, false, true],
                    margin: [0, 5, 0, 5],
                  },
                  {
                    text: this.getTotalPrice().toFixed(2) + ' €',
                    bold: true,
                    fontSize: 20,
                    alignment: 'right',
                    border: [false, false, false, true],
                    fillColor: '#f5f5f5',
                    margin: [0, 5, 0, 5],
                  },
                ],
              ],
            },
          },
        ],
        styles: {
          notesTitle: {
            fontSize: 10,
            bold: true,
            margin: [0, 50, 0, 3],
          },
          notesText: {
            fontSize: 10,
          },
        },
        defaultStyle: {
          columnGap: 20,
          //font: 'Quicksand',
        },
      };
      this.openPDF(docDefinition);
    }
  }

  async openPDF(docDefinition: any) {
    if (this.userId) {
      this.loading = true;
      pdfMake.createPdf(docDefinition).open();
      await pdfMake.createPdf(docDefinition).getBuffer((buffer) => this.uploadPDF(buffer));
    }
  }

  resetPositions() {
    this.position_for_pdf = [];
    this.positions = [];
  }

  async uploadPDF(buffer: any) {
    this.user.invoices.push({ price: this.getTotalPrice(), invoiceNumber: this.newInvoiceNumber, date: this.date });
    this.saveInvoice(buffer);
    this.resetPositions();
  }

  saveInvoice(buffer: any) {
    this.fireStorage.upload(`invoices/` + this.userId + `/000${this.newInvoiceNumber}.pdf`, buffer)
      .then(() => this.saveInvoiceAll(buffer));
  }

  saveInvoiceAll(buffer: any) {
    this.fireStorage.upload(`invoices/all/000${this.newInvoiceNumber}.pdf`, buffer)
      .then(() => this.updateUserInvoice());
  }

  updateUserInvoice() {
    this.firestore.collection('customer')
      .doc(this.userId)
      .update(this.user.toJson())
      .then(() => {
        this.loading = false;
        this.newInvoiceNumber++;
        this.myForm.reset();
      });
  }
}